import Link from 'next/link';
const SingleArticleView = (data)=>{
    return (
       <div >

           <h4 key={data.data.id}><span>{data.data.title}</span></h4>
           <h6>{data.data.date} Â· {data.data.time} read</h6>
           <ul className="keywords">
           {data.data.keyWords.split(', ').map((word,i)=>(
              <li key={word[i]}>
                  {word}
              </li> 
           ))}
           <h6><a href={`articles/${data.data.article}`}>Read more on Medium</a></h6>
           </ul>

        <style jsx>
        {`h4{
                letter-spacing: 1px;
                font-family: 'Lato', sans-serif;
                color:#424242;
                text-align: center;
                align-text:center;
                padding-top: 20px;
                padding-bottom: none;
                marging-bottom: none;
            }
         .keywords {
            letter-spacing: 1px;
            font-family: 'Lato', sans-serif;
            color:#424242;
            text-align: center;
            align-text:center; 
            font-size: small;
            list-style: none;
         }
         h6{
            letter-spacing: 1px;
            font-family: 'Lato', sans-serif;
            color:#bdbdbd;
            text-align: center;
            align-text:center;
            padding: 5px;
            marging:none;
         }
            `}
        </style>
       </div>
    )
}
export default SingleArticleView
