import Link from 'next/link';
const SingleArticleView = (data)=>{
    return (
       <div>
           <h5 key={data.data.id}>{data.data.title}</h5>
           <ul>
           {data.data.keyWords.split(', ').map(word=>(
              <li className="keywords">
                  {word}
              </li> 
           ))}
           </ul>

        <style jsx>
        {`h5{

                letter-spacing: 1px;
                font-family: 'Lato', sans-serif;
                color:#424242;
                text-align: center;
                margin-bottom:5%;
                align-text:center;
                padding: 5%;
            }
         .keywords {
            letter-spacing: 1px;
            font-family: 'Lato', sans-serif;
            color:#424242;
            text-align: center;

            align-text:center;

         } 
            `}
        </style>
       </div>
    )
}
export default SingleArticleView
