import Layout from '../components/Layout';
const About = () =>(
    <Layout>
        <div>
            <h4>About me</h4>
            <style jsx>
                {`@import url('https://fonts.googleapis.com/css?family=Lato');
            h4{
                text-transform: uppercase;
                letter-spacing: 2px;
                font-family: 'Lato', sans-serif;
                color:#424242;
                text-align: center;
            }`}
            </style>
        </div>
        <div className="navigation-area">
            <div className="course">
                <h3 className="title">Skills</h3>
                <ul>
                    <li>
                        <div>
                            <h1>Programming languages: </h1>
                            <p> Python · JavaScript</p>
                        </div>
                    </li>
                    <li>
                        <div>
                            <h1>Full Stack:</h1>
                            <p> React · Redux · Node.js · Express</p>
                        </div>
                    </li>
                    <li>
                        <div>
                            <h1>Database: </h1>
                            <p> PostgreSQL (data analyses) · Sequelize · Relational Model · Normalization · Firebase</p>
                        </div>
                    </li>
                </ul>
            </div>
            <style jsx>
                {`.navigation-area{
                width: 80%;
                padding: 50px 0;
                margin: 50px auto;
                position: relative;
                overflow: hidden;
            }
            .course{
                padding-top: 3em;
                padding-left: 1em;
                margin-left: 5em;
                border-left: 1px solid rgb(239, 239, 239);
            }
            .marker{
                display: inline-block;
                margin-left: -2%;
                border-radius: 50%;
                transition: background 0.5s ease;
                z-index: 9999;
                width: 5px;
                height: 5px;
                border: 1px solid rgb(239, 239, 239);
                background: rgb(239, 239, 239);
                margin-top: 15px;
                position:absoulute;
            }

                ul{
                    width:500px;
                    height:auto;
                    margin:100px auto;
                    border:0px solid #111;
                    padding:10px;
                    position:relative;
                    list-style:none;
                    }
                    ul li{
                    padding-bottom:50px;
                    }
                    ul:before{
                    content:"";
                    position:absolute;
                    width:5px;
                    background:#aaa69d;
                    height:95%;
                    left:0px;
                    top:0;
                    }
                    ul li:before{
                    position:absolute;
                    content:'';
                    width:20px;
                    height:20px;
                    background:#ff5252;
                    left:-8px;
                    margin-top:20px;
                    border-radius:50%;
                    }
                    ul li div{
                    padding:20px;
                    padding-bottom:50px;
                    margin-left:30px;
                    position:relative;
                    background:#d1ccc0;
                    box-shadow:0px 15px 15px -10px #d1ccc0
                    }
                    ul li div h1{
                    margin-bottom:20px
                    }
                `}
            </style>
        </div>
    </Layout>
)
export default About
