import Link from 'next/link';
import redirect from 'nextjs-redirect'
const SingleArticleView = (data)=>{
    return (
       <div >
           <h5 key={data.data.id}><span><Link href={`articles/${data.data.redirection}`}><a>{data.data.title}</a></Link></span></h5>
           <h6>{data.data.date}</h6>
           <ul className="keywords">
           {data.data.keyWords.split(', ').map((word,i)=>(
              <li key={word[i]}>
                  {word}
              </li> 
           ))}
           </ul>

        <style jsx>
        {`h5{

                letter-spacing: 1px;
                font-family: 'Lato', sans-serif;
                color:#424242;
                text-align: center;

                align-text:center;
                padding: 10px;
            }
         .keywords {
            letter-spacing: 1px;
            font-family: 'Lato', sans-serif;
            color:#424242;
            text-align: center;
            align-text:center; 
            font-size: small;
            list-style: none;
         }
         .h6{
            letter-spacing: 1px;
                font-family: 'Lato', sans-serif;
                color:#424242;
                text-align: center;

                align-text:center;
                padding: 10px;
         }
            `}
        </style>
       </div>
    )
}
export default SingleArticleView
